import React, {Component} from 'react';
import Header from "../../componets/header";
import Seadbar from "../../componets/seadbar";
import {Redirect, Link ,useHistory} from 'react-router-dom';
import axios from 'axios';









class  add extends Component
{

    handleSubmit = (e) => {
        e.preventDefault();
        
        const data = {
          firstname: this.firstname,
          lastname: this.lastname,
          username: this.username,
          email: this.email,
          password: this.password,
          address: this.address,
          tel: this.tel,
          date_naiss: this.date_naiss,
          role_id: this.role_id,
          img: this.img
        };

    
        axios.post('http://127.0.0.1:8000/api/user',data).then(res =>{
    
                       
        }).catch(err=>{


        })
    
      
    
    
      };

 
 


render(){

    

    return (
       <div>
           <Header />
           <Seadbar />


         <div className="content-wrapper">
  {/* Content Header (Page header) */}
  <section className="content-header">
    <div className="container-fluid">
      <div className="row mb-2">
        <div className="col-sm-6">
          <h1>New User</h1>
        </div>
        <div className="col-sm-6">
          <ol className="breadcrumb float-sm-right">
            <li className="breadcrumb-item"><a href="#">Home</a></li>
            <li className="breadcrumb-item active">User</li>
          </ol>
        </div>
      </div>
    </div>{/* /.container-fluid */}
  </section>
  {/* Main content */}
  <section className="content">
    <div className="container-fluid">
      <div className="row">
        {/* left column */}
        <div className="col-md-12">
          {/* general form elements */}
          <div className="card card-secondary">
            <div className="card-header">
              <h3 className="card-title">User</h3>
            </div>
            {/* /.card-header */}
            {/* form start */}
            <form onSubmit={this.handleSubmit}>
              <div className="card-body">

              <div className="row">
                  <div className="col-sm-6">
                    {/* text input */}
                    <div className="form-group">
                      <label>Firstname</label>
                      <input type="text" className="form-control" placeholder="Enter ..." onChange={e=>this.firstname=e.target.value} />
                    </div>
                  </div>
                  <div className="col-sm-6">
                    <div className="form-group">
                      <label>LastName</label>
                      <input type="text" className="form-control" placeholder="Enter ..." onChange={e=>this.lastname=e.target.value} />
                    </div>
                  </div>
              </div>

              <div className="row">
                  <div className="col-sm-6">
                    {/* text input */}
                    <div className="form-group">
                      <label>Username</label>
                      <input type="text" className="form-control" placeholder="Enter ..." onChange={e=>this.username=e.target.value}/>
                    </div>
                  </div>
                  <div className="col-sm-6">
                    <div className="form-group">
                      <label>Email</label>
                      <input type="email" className="form-control" placeholder="Enter ..." onChange={e=>this.email=e.target.value}/>
                    </div>
                  </div>
              </div>

              <div className="form-group">
                  <label htmlFor="exampleInputPassword1">Password</label>
                  <input type="password" className="form-control"  placeholder="Password" onChange={e=>this.password=e.target.value} />
              </div>

              <div className="row">
                  <div className="col-sm-6">
                    {/* text input */}
                    <div className="form-group">
                      <label>address</label>
                      <input type="text" className="form-control" placeholder="Enter ..." onChange={e=>this.address=e.target.value}/>
                    </div>
                  </div>
                  <div className="col-sm-6">
                    <div className="form-group">
                      <label>Brithdays</label>
                      <input type="date" className="form-control" placeholder="Enter ..." onChange={e=>this.date_naiss=e.target.value} />
                    </div>
                  </div>
              </div>

              <div className="row">
                  <div className="col-sm-6">
                    {/* text input */}
                    <div className="form-group">
                      <label>Telephone</label>
                      <input type="text" className="form-control" placeholder="Enter ..." onChange={e=>this.tel=e.target.value}/>
                    </div>
                  </div>

                  <div className="col-sm-6">
                    <div className="form-group">
                      <label>Stattus</label>
                      <select className="form-control" onChange={e=>this.role_id=e.target.value} >
                        <option value="1">Admin</option>
                        <option value="2">Manager</option>
                        <option value="3">Technicien</option>
                      </select>
                    </div>
                  </div>
                </div>



                <div class="form-group">
                    <label for="customFile">Custom File</label> 

                    <div class="custom-file">
                      <input type="file" class="custom-file-input" id="customFile" onChange={e=>this.img=e.target.value} />
                      <label class="custom-file-label" for="customFile">Choose file</label>
                    </div>
                  </div>

                
               
              </div>
              {/* /.card-body */}
              <div className="card-footer">
                <button type="submit" className="btn btn-primary">Submit</button>
              </div>
            </form>
          </div>
          {/* /.card */}
          {/* general form elements */}
          {/* /.card */}
          {/* Input addon */}
            {/* /.card-body */}
          {/* /.card */}
          {/* Horizontal Form */}
          {/* /.card */}
        {/*/.col (left) */}
        {/* right column */}
          {/* /.card */}
          {/* general form elements disabled */}
            {/* /.card-body */}
          
          
        {/*/.col (right) */}
      {/* /.row */}
      </div>
      </div>
      </div>


  </section>
  {/* /.content */}
</div>


           
          
</div>
            
    

    );
              
  }
}

export default add;
