import React, {Component} from 'react';
import Header from "../../componets/header";
import Seadbar from "../../componets/seadbar";
import {Redirect, Link ,useHistory} from 'react-router-dom';
import axios from 'axios';
import { ToastContainer, toast} from 'react-toastify';
import  'react-toastify/dist/ReactToastify.css';







class  Add extends Component
{

    handleSubmit = (e) => {
        e.preventDefault();

        let formdata = new FormData()


        formdata.append('firstname',this.firstname)
        formdata.append('lastname',this.lastname)
        formdata.append('username',this.username)
        formdata.append('email',this.email)
        formdata.append('password',this.password)
        formdata.append('address',this.address)
        formdata.append('tel',this.tel)
        formdata.append('date_naiss',this.date_naiss)
        formdata.append('role_id',this.role_id)
        formdata.append('img',this.img)
    
        axios.post('http://127.0.0.1:8000/api/user',formdata).then(res =>{

            toast.success(res.data.message)
            setTimeout(() => {
              this.props.history.push('/User')
            }, 5000)               
        }).catch(err=>{


        })
    
      
    
    
      };

 
 


render(){

    

    return (
       <div>
           <Header />
           <Seadbar />


         <div className="content-wrapper">
  {/* Content Header (Page header) */}
  <section className="content-header">
    <div className="container-fluid">
      <div className="row mb-2">
        <div className="col-sm-6">
          <h1>Nouveau</h1>
        </div>
        <div className="col-sm-6">
          <ol className="breadcrumb float-sm-right">
            <li className="breadcrumb-item"><a href="#">Acceuil</a></li>
            <li className="breadcrumb-item active">Materiel</li>
          </ol>
        </div>
      </div>
    </div>{/* /.container-fluid */}
  </section>
  {/* Main content */}
  <section className="content">
    <div classname="container-fluid">
  <div className="row">
    <div className="col-md-12">
      <div className="card card-default">
        <div className="card-header">
          <h3 className="card-title">bs-stepper</h3>
        </div>
        <div className="card-body p-0">
          <div className="bs-stepper">
              


          <div className="row">
                  <div className="col-sm-6">
                    {/* text input */}
                    <div className="form-group">
                      <label>Prenoms</label>
                      <input type="text" className="form-control" placeholder="Enter ..." onChange={e=>this.firstname=e.target.value} />
                    </div>
                  </div>
                  <div className="col-sm-6">
                    <div className="form-group">
                      <label>Nom</label>
                      <input type="text" className="form-control" placeholder="Enter ..." onChange={e=>this.lastname=e.target.value} />
                    </div>
                  </div>
              </div>
            
          </div>
          
           
          </div>

          </div>
          </div>
          </div>
          </div>

               
        
        
     


  </section>
</div>


<ToastContainer/>
   
          
</div>
            
    

    );
              
  }
}

export default Add;
